import React from 'react';
import {
     Button,
} from '@momentum-ui/react';
import {
    Redirect,
} from 'react-router-dom';
import Header from './Header.js';

class Home extends React.Component {

    constructor(props) {
        super(props);
        this.state = {

        };
        this.addClasses = this.addClasses.bind(this);
    }

    componentDidMount() {
    }

    addClasses = (event) => {
        this.props.history.push('/classroomSetup');
    }

    render() {
        const token = sessionStorage.getItem('access_token');
        if (token === null) {
            return <Redirect to='/'/>;
        }

        // const classList = localStorage.getItem('classList');
        let classList = [{
            name: '1st Period: Algebra',
            time: '8:40AM - 9:30AM, Daily (M-F)',
            classId: 1
        }];

        const hideGrid = {
            marginTop: 20
        };

        return (
            <div className="grid" style={{paddingTop:20}}>
                <Header />
                <div className="row large-offset-1">
                    <div className="columns large-6" style={hideGrid}>
                        For each class, we'll walk you through the set-up process. Youll need names and email addresses of students for the set-up process.
                    </div>
                </div>
                <div className="row large-offset-1">
                    <div className="large-3 columns" style={hideGrid}>
                        <Button
                            children='Add Class'
                            color='blue'
                            onClick={this.addClasses}
                            ariaPressed={false}
                          />
                    </div>
                </div>
                <div className="row" style={classList === null ? {hideGrid} : {display: "none"}}>
                    <div className="large-8 large-centered columns">
                        <img  alt="addClass" src="addClass.png"/>
                    </div>
                </div>
                <div className="row" style={classList === null ? {display: "none"} : {}}>
                    <h1>Classes</h1>
                </div>
                {classList !== null ? classList.map(item => (
                    <div className="row large-offset-1"  key={item.classId}>
                        <div className="large-3 columns">
                            {item.name}
                            <p>{item.time}</p>
                        </div>
                        <div className="large-9 columns">
                            <Button
                                children='Open in Teams'
                                color='blue'
                                onClick={this.addClasses}
                                ariaPressed={false}
                              />
                        </div>
                    </div>
                )) : <div></div>}
            </div>
        );
    }
}

export default Home;

