const API_URL = 'https://petee.pegatsdemo.com/prweb/PRRestService/';
const CLIENT_ID = '11185500654705020911';
const CLIENT_SECRET = '2C2ACD29C9B394F67F53C1C13559558C';

export function requestAccessToken(delegate) {
    const authInfo = `grant_type=client_credentials&client_id=${CLIENT_ID}&client_secret=${CLIENT_SECRET}`;
    const requestOptions = {
        method: 'POST',
        headers: new Headers({
            'Content-Type': 'application/x-www-form-urlencoded',
        }),
        body: authInfo,
    };

    return fetch(`${API_URL}oauth2/v1/token`, requestOptions)
    .then(response => response.json())
    .then(response => sessionStorage.setItem('access_token',response.access_token))
    .then(response => setTimeout(delegate,100));
}

export function requestCreateSpace(spaceInfo, delegate) {
    const token = sessionStorage.getItem('access_token');
    const requestOptions = {
        method: 'GET',
        headers: new Headers({
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
        }),
    };

    return fetch(`${API_URL}ControllerPilot/v1/CreateSpace`, requestOptions)
    .then(response => response.json())
    .then(response => console.log(response))
    .then(response => setTimeout(delegate,100));
}
