import React from 'react';
import { Avatar, Input, Button } from '@momentum-ui/react';

class Home extends React.Component {
    constructor() {
        super();
        this.state = {
            mail: '',
            emailEntered: true,
            error: true
        };
        this.validateEmail = this.validateEmail.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    textChange = text => {
        this.setState( {mail: text.target.value} )
        this.validateEmail();
    }

    validateEmail() {
        this.setState({error: false});
        let email = this.state.mail;
        if (email === '') {
            this.setState({error: true});
        }
        let lastAtPos = email.lastIndexOf('@');
        let lastDotPos = email.lastIndexOf('.');
        if (!(lastAtPos < lastDotPos &&
              lastAtPos > 0 &&
              email.indexOf('@@') === -1 &&
              lastDotPos > 2 &&
             (email.length - lastDotPos) > 2)) {
                 this.setState({error: true});
        }
    }

    onSubmit = (event) => {
        this.validateEmail();
        if (!this.state.error) {
            this.props.history.push({
                pathname: '/login',
                mail: this.state.mail
            });
        } else {
            alert('Please enter valid email address!');
        }
    }

    render() {
        return (
            <div className="grid" style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '100vh'}}>
              <div className="row">
                <div className="medium-4 large-offset-1 columns">
                    <p style={{fontSize:22}}>Cisco Webex Teams </p>
                    <p style={{fontSize:30}}>Seamlessly take your classroom virtual.</p>
                    <p style={{fontSize:16}}>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                        Suspendisse molestie in felis id tincidunt. Ut suscipit vestibulum sagittis.
                        Nam ultrices porta sapien, sed tincidunt urna egestas id.
                        Sed finibus felis eget turpis posuere, sed dapibus dui pretium.
                    </p>
                    <div className="row" style={{marginTop:20}}>
                      <div className="medium-8 columns">
                          <Input
                              name='defaultInput'
                              type='email'
                              inputSize='small-15'
                              placeholder='Enter your work Email address'
                              onChange={this.textChange}
                              style={{borderRadius:'18px'}}
                            />
                      </div>
                      <div className="medium-4 columns">
                          <Button
                              children='Start for free'
                              color='blue'
                              disabled={this.state.error}
                              onClick={this.onSubmit}
                            />
                      </div>
                    </div>
                    <p style={{fontSize:11, paddingTop:10}}>
                      By clicking 'Start for free' you understand and agree to the
                      <a href="https://www.cisco.com/c/en/us/about/legal/terms-conditions.html"> Cisco Terms of Services.</a>.
                    </p>
                </div>
                <div className="medium-6 columns">
                    <img  src="story.png" style={{width:500, height:350}} alt="Simple Story Illustration"/>
                </div>
              </div>
            </div>
        );
    }
}

export default Home;

