import React from 'react';
import {
    Link
} from 'react-router-dom';

// import story from "../story.png"

class Home extends React.Component {
    state = { mail: '' }

    textChange = text =>
        this.setState( {mail: text.target.value} )

    render() {
        return (
            <>
            <div class="home_page_div">
                <section class="home_page_section left">
                    <p style={{fontSize:22}}>Cisco Webex Teams </p>
                    <p style={{fontSize:30}}>Seamlessly take your classroom virtual.</p>
                    <p style={{fontSize:16}}>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                        Suspendisse molestie in felis id tincidunt. Ut suscipit vestibulum sagittis.
                        Nam ultrices porta sapien, sed tincidunt urna egestas id.
                        Sed finibus felis eget turpis posuere, sed dapibus dui pretium.
                    </p>
                    <p>
                        <input
                        type="text"
                        onChange={this.textChange}
                        class="text_field_email"
                        />
                        <Link to={{
                            pathname: '/login',
                            mail: this.state.mail
                        }}>
                        <button type="button" className="btn_start_for_free"> Start for Free </button>
                        </Link>
                    </p>
                    <p style={{fontSize:11, paddingTop:10}}>
                      By clicking 'Start for freeâ€™ you understand and agree to the
                      <a href="https://www.cisco.com/c/en/us/about/legal/terms-conditions.html"> Cisco Terms of Services.</a>.
                    </p>
                </section>
                <section class="home_page_section right">
                    <img  src="story.png" style={{width:500, height:350}} />
                </section>
            </div>
            </>
        );
    }
}

export default Home;

