import React from 'react';
import './App.css';
import Landing from './pages/Landing.js';
import Login from './pages/Login.js';
import Home from './pages/Home.js';
import GeneralForm from './pages/GeneralForm.js';
import FileUpload from './pages/FileUpload.js';
import ManualEntry from './pages/ManualEntry.js';
import Review from './pages/Review.js';
import Complete from './pages/Complete.js';
import NotFound from './pages/NotFound.js'
import {
    BrowserRouter as Router,
    Route,
    Redirect,
    Switch
} from 'react-router-dom';

const checkAuthentication = {
    isValid: false,
    login() {
        checkAuthentication.isValid = true;
    },
    logout() {
        checkAuthentication.isValid = false;
    }
};

function PrivateRoute({ children, ...rest }) {

    return (
        <Route
          {...rest}
          render={({ location }) =>
            checkAuthentication.isValid ? (
              children
            ) : (
              <Redirect
                to={{
                  pathname: "/",
                  state: { from: location }
                }}
              />
            )
          }
        />
    );
}

class App extends React.Component {

    constructor() {
        super();
    }

    render() {
        return (
            <Router>
                <div className="App">
                  <div id="page-id">
                  <Switch>
                        <Route path="/" component={Landing} exact/>
                        <PrivateRoute path="/addClasses">
                          <Home />
                        </PrivateRoute>
                        <PrivateRoute path="/classroomSetup">
                          <GeneralForm />
                        </PrivateRoute>
                        <PrivateRoute path="/fileUpload">
                          <FileUpload />
                        </PrivateRoute>
                        <PrivateRoute path="/addStudents">
                          <ManualEntry />
                        </PrivateRoute>
                        <PrivateRoute path="/review">
                          <Review />
                        </PrivateRoute>
                        <Route path="/classroomReady" component={Complete} />
                        <Route component={NotFound} />
                    </Switch>
                  </div>
                </div>
            </Router>
        );
    }
}

export default App;
