import React from 'react';
import {
    Avatar,
    Button,
} from '@momentum-ui/react';

class Review extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
        };
        this.saveClick = this.saveClick.bind(this);
        this.cancelClick = this.cancelClick.bind(this);
    }

    cancelClick(e) {
        this.props.history.push({
            pathname: '/addStudents',
            classObj: this.props.location.classObj,
            studentList: this.props.location.studentList,
            uploadedFile: this.props.location.uploadedFile,
        });
    }

    saveClick(e) {
        this.props.history.push({
            pathname: '/classroomReady',
        });
    }

    render() {
        const hideGrid = {
            marginTop: 20
        };

        const tabBar = {marginTop: 20,
                        height: 40,
                        textAlign: 'center',
                        border: '1px solid #e5e5e5',
        };
        const active = {marginTop: 20,
                        height: 40,
                        textAlign: 'center',
                        border: '1px solid #e5e5e5',
                        backgroundColor: '#e6f9fc'
        };

        const obj = this.props.location.classObj;
        const studentObj = this.props.location.studentList;

        return (
            <div className="grid" style={{paddingTop:20}}>
                <div className="row large-offset-1">
                    <div className="columns large-11" style={hideGrid}>
                        <h1>Classroom Set Up</h1>
                    </div>
                    <div className="columns large-1 columns_new" style={hideGrid}>
                        <Avatar title="Barbara" src="http://react.collab-ui.com/barbara.png"/>
                    </div>
                </div>
                <div className="row large-offset-1">
                    <div className="columns large-3" style={tabBar}>
                            <h3>General Info</h3>
                    </div>
                    <div className="columns large-3" style={tabBar}>
                        <h3>Add Students(Upload)</h3>
                    </div>
                    <div className="columns large-3" style={tabBar}>
                        <h3>Add Students(Manual)</h3>
                    </div>
                    <div className="columns large-3" style={active}>
                        <h3>Review</h3>
                    </div>
                </div>
                <div className="row">
                    <div className="columns large-10" style={hideGrid}>
                        <p>Please review the information for the class to finish the set-up process.</p>
                    </div>
                </div>
                <div className="row">
                    <div className="columns large-10" style={hideGrid}>
                        <h3>General Info:</h3>
                        Class Name: {obj.className}
                        Course Number: {obj.courseNumber}
                        Period: {obj.period}
                        Time: `${obj.startTime} - ${obj.endTime}`
                        Recurrence: {obj.recurrence}
                        Last Day of Class: {obj.date}
                    </div>
                </div>
                <div className="row large-offset-1" style={{marginTop:40, marginLeft:10}}>
                    <div className="large-2 columns">
                        <Button
                            children='Back'
                            color='blue-outline'
                            onClick={this.cancelClick}
                            style={{color:'blue',borderRadius:5}}
                            ariaPressed={false}
                          />
                    </div>
                    <div className="large-1 columns">
                        <Button
                            children='Finish'
                            color='blue'
                            onClick={this.saveClick}
                            ariaPressed={false}
                          />
                    </div>
                </div>
            </div>
        );
    }
}

export default Review;
