import React from 'react';
import Webex from 'webex';

    const webex = window.webex = Webex.init({
      config: {
        credentials: {
          client_id: 'C190e9a362204470aa77adb388e66e78ca175ac471e215ce269540297660be61e',
          redirect_uri: 'http://localhost:3000/addClasses',
          scope: 'spark:all spark:kms'
        }
      }
    });

class Login extends React.Component {

    constructor(props) {
        super(props);
        this.checkWebex();
        this.state = {
            mail: props.match.params.mail,
            loggedIn: true
        }
        console.log(props);
    }

    checkWebex() {
        if (webex.canAuthorize) {
              // if already authenticated, logout on click
              webex.logout();
              this.setState({loggedIn: true});
            }
         // webex.authorization.initiateLogin();
         this.setState({loggedIn: false});
    }

    render() {
        console.log(this.props);
        return (
            <>
            <h1>{this.state.loggedIn ? "True" : "False"}</h1>
            <h2>{this.state.mail}</h2>
            </>
        );
    }
}
// const Login = (  ) => {
// }

export default Login;
