import React from 'react';
import { Avatar,
        Button,
} from '@momentum-ui/react';
import readXlsxFile from 'read-excel-file'

class FileUpload extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            uploadedFile: null,
            studentList: null,
            lastIndex: 1,
        }
        this.fileRef = React.createRef();
        this.uploadClicked = this.uploadClicked.bind(this);
        this.onSelectFile = this.onSelectFile.bind(this);
        this.saveClick = this.saveClick.bind(this);
        this.cancelClick = this.cancelClick.bind(this);
    }

     componentDidMount(props) {
         const fileObj = this.props.location.uploadedFile;
         const studentObj = this.props.location.studentList;
         if (fileObj !== undefined) {
             this.setState({ uploadedFile: fileObj });
         }
         if (studentObj !== undefined) {
             this.setState({ studentList: studentObj });
         }
     }

    uploadClicked(e) {
        console.log(this.state.uploadedFile);
        if (this.state.uploadedFile === null ) {
            this.fileRef.current.click();
        } else {
            this.setState({
                uploadedFile: null,
            });
        }
    }

    onSelectFile(e) {
        e.stopPropagation();
        e.preventDefault();
        var file = e.target.files[0];
        this.setState({
            uploadedFile: file.name,
        });
        const schema = {
                        'First Name': {
                            prop: 'firstName',
                            type: String,
                            required: true
                        },
                        'Last Name': {
                            prop: 'lastName',
                            type: String,
                            required: true
                        },
                        'Email': {
                            prop: 'email',
                            type: String,
                            required: true
                        },
                    }
        readXlsxFile(file, { schema })
        .then(({ rows, errors }) => {
            if (errors.length === 0) {
                const students = rows.map(item => {
                    item.studentId = this.state.lastIndex;
                    this.setState({ lastIndex: this.state.lastIndex + 1 });
                    return item;
                });

                this.setState({
                  studentList: students
                });
            } else {
                alert('Invalid file. Kindly follow file Template!')
            }
        });
    }

    downloadClicked(e) {
        window.location.href=`${process.env.PUBLIC_URL}/studentList.xlsx`;
    }

    cancelClick(e) {
        this.props.history.push({
            pathname: '/classroomSetup',
            classObj: this.props.location.classObj,
            studentList: this.state.studentList,
            uploadedFile: this.state.uploadedFile,
        });
    }

    saveClick(e) {
        this.props.history.push({
            pathname: '/addStudents',
            classObj: this.props.location.classObj,
            studentList: this.state.studentList,
            uploadedFile: this.state.uploadedFile,
        });
    }

    render() {
        const hideGrid = {
            marginTop: 20
        };
        const tabBar = {marginTop: 20,
                        height: 40,
                        textAlign: 'center',
                        border: '1px solid #e5e5e5',
        };
        const active = {marginTop: 20,
                        height: 40,
                        textAlign: 'center',
                        border: '1px solid #e5e5e5',
                        backgroundColor: '#e6f9fc'
        };

        let uploadedFile = this.state.uploadedFile;
        const fileObj = this.props.location.uploadedFile;
        if (fileObj !== undefined) {
            uploadedFile = fileObj;
        }

        return (
            <div className="grid" style={{paddingTop:20}}>
                <div className="row large-offset-1">
                    <div className="columns large-11" style={hideGrid}>
                        <h1>Classroom Set Up</h1>
                    </div>
                    <div className="columns large-1 columns_new" style={hideGrid}>
                        <Avatar title="Barbara" src="http://react.collab-ui.com/barbara.png"/>
                    </div>
                </div>
                <div className="row large-offset-1">
                    <div className="columns large-3" style={tabBar}>
                        <h3>General Info</h3>
                    </div>
                    <div className="columns large-3" style={active}>
                        <h3>Add Students(Upload)</h3>
                    </div>
                    <div className="columns large-3" style={tabBar}>
                        <h3>Add Students(Manual)</h3>
                    </div>
                    <div className="columns large-3" style={tabBar}>
                        <h3>Review</h3>
                    </div>
                </div>
                <div className="row">
                    <div className="columns large-6" style={hideGrid}>
                        <div className="row" style={{marginLeft: 10}}>
                            <p style={{fontSize:16}}>Upload Students information from an excel file (.XLS or .XLSX). Files
                            must adhere to the format shown to the right.</p>
                        </div>
                        <div className="row" style={{marginTop: 30, marginLeft: 10}}>
                            <t style={uploadedFile === null ? {display: "none"} : {marginRight: 20}}>{uploadedFile}</t>
                            <input type="file" ref={this.fileRef} style={{display: "none"}} onChange={this.onSelectFile}/>
                            <Button
                                children={uploadedFile === null ? 'Upload from file' : 'Delete'}
                                color='blue'
                                onClick={this.uploadClicked}
                                ariaPressed={false}
                              />
                              <Button
                                  children='Download Template'
                                  color='blue-outline'
                                  onClick={this.downloadClicked}
                                  ariaPressed={false}
                                />
                         </div>
                    </div>
                    <div className="columns large-6" style={hideGrid}>
                        <img  alt="fileImageHolder" src="fileImageHolder.png"/>
                    </div>
                </div>
                <div className="row large-offset-1" style={{marginTop:40, marginLeft:10}}>
                    <div className="large-2 columns">
                        <Button
                            children='Back'
                            color='blue-outline'
                            onClick={this.cancelClick}
                            style={{color:'blue',borderRadius:5}}
                            ariaPressed={false}
                          />
                    </div>
                    <div className="large-1 columns">
                        <Button
                            children='Continue'
                            color='blue'
                            onClick={this.saveClick}
                            ariaPressed={false}
                          />
                    </div>
                </div>
            </div>
        );
    }
}

export default FileUpload;
