import React from 'react';
import {
    Input,
    Button,
    EditableTextfield,
} from '@momentum-ui/react';
import { findIndex, without } from 'lodash';

class ManualEntry extends React.Component {
    constructor() {
        super();

        this.state = {
            students: [

                    ],
            firstName: '',
            lastName: '',
            email: '',
            lastIndex: 1,
            firstNameError: {},
            lastNameError: {},
            emailError: {},
            fileName: '',
        };
        this.addInfo = this.addInfo.bind(this);
        this.updateInfo = this.updateInfo.bind(this);
        this.deleteInfo = this.deleteInfo.bind(this);
        this.onTextChange = this.onTextChange.bind(this);
    }

    componentDidMount() {
        const stds = this.state.students.map(item => {
            item.stdId = this.state.lastIndex;
            this.setState({ lastIndex: this.state.lastIndex + 1 });
            return item;
        });
        this.setState({
          students: stds
        });
    }

    validateEmail() {
        this.setState({error: false});
        let email = this.state.mail;
        if (email === '') {
            this.setState({error: true});
        }
        let lastAtPos = email.lastIndexOf('@');
        let lastDotPos = email.lastIndexOf('.');
        if (!(lastAtPos < lastDotPos &&
              lastAtPos > 0 &&
              email.indexOf('@@') === -1 &&
              lastDotPos > 2 &&
             (email.length - lastDotPos) > 2)) {
                 this.setState({error: true});
        }
    }

    addInfo(e) {
        e.preventDefault();
        if (this.state.firstName === '') {
            this.setState({
                firstNameError: { message: 'Please enter student first name.', type: 'error' },
            });
        } else if (this.state.lastName === '') {
            this.setState({
                lastNameError: { message: 'Please enter student last name.', type: 'error' },
            });
        } else if (this.state.email === '') {
            this.setState({
                emailError: { message: 'Please enter student email.', type: 'error' },
            });
        } else {
            let newStudent = {
                firstName: this.state.firstName,
                lastName: this.state.lastName,
                email: this.state.email,
                stdId: this.state.lastIndex,
            };
            let temp = this.state.students;
            temp.unshift(newStudent);
            this.setState({
                students: temp,
                lastIndex: this.state.lastIndex + 1,
            });
            this.setState({
              firstName: '',
              lastName: '',
              email: '',
            });

        }
    }

    cancelClick(e) {

    }

    saveClick(e) {

    }

    onTextChange(e) {
        const target = e.target;
        const value = target.value;
        const name = target.name;

        let errorMsg = {};
        if (value === '') {
            errorMsg = { message: 'Please enter student information', type: 'error' };
        }
        this.setState({
          [name]: value,
          [`${name}Error`]: errorMsg,
        });

    }

    updateInfo(name, value, id) {
        let temp = this.state.students;
        console.log(temp);
        let stdIndex = findIndex(this.state.students, {
          stdId: id
        });
        temp[stdIndex][name] = value;
        this.setState({
          students: temp
        });
        console.log(this.state.students);
    }

    deleteInfo(item) {
        let temp = this.state.students;
        temp = without(temp, item);

        this.setState({
          students: temp
        });
    }

    render() {
        const hideGrid = {
            marginTop: 20
        };

        return (
            <div className="grid" style={{paddingTop:20}}>
                <div className="row">
                    <div className="columns large-10" style={hideGrid}>
                        <p style={{fontSize:16}}>Enter Students’ information manually.</p>
                    </div>
                </div>
                {this.state.students.map(item => (
                    <div  key={item.stdId}>
                        <div className="row"  key={item.stdId}>
                            <div className="columns large-2" style={hideGrid}>
                                <EditableTextfield
                                        handleDoneEditing={(e, data) => this.updateInfo('firstName',data.value,item.stdId)}
                                        inputText={item.firstName}
                                      />
                            </div>
                            <div className="columns large-2" style={hideGrid}>
                                <EditableTextfield
                                        handleDoneEditing={(e, data) => this.updateInfo('lastName',data.value,item.stdId)}
                                        inputText={item.lastName}
                                      />
                            </div>
                            <div className="columns large-2" style={hideGrid}>
                                <EditableTextfield
                                        handleDoneEditing={(e, data) => this.updateInfo('email',data.value,item.stdId)}
                                        inputText={item.email}
                                      />
                            </div>
                            <div className="columns large-5" style={hideGrid}>
                                <Button
                                    children='Delete'
                                    color='blue-outline'
                                    onClick={(e) => this.deleteInfo(item)}
                                    style={{color:'blue',borderRadius:5}}
                                  />
                            </div>
                        </div>
                        <div className="row">
                            <hr style={{marginLeft: 20, height: 1, width: '95%', border: 'none', color: 'grey', backgroundColor: 'grey'}}></hr>
                        </div>
                    </div>
                ))}
                <div className="row">
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='firstName'
                            label='First Name'
                            type='text'
                            inputSize='large-15'
                            placeholder='Emily'
                            onChange={this.onTextChange}
                            messageArr={[ this.state.firstNameError ]}
                          />
                    </div>
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='lastName'
                            label='Last Name'
                            type='text'
                            inputSize='large-15'
                            placeholder='Henning'
                            onChange={this.onTextChange}
                            messageArr={[ this.state.lastNameError ]}
                          />
                    </div>
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='email'
                            label='Email'
                            type='email'
                            inputSize='large-15'
                            placeholder='emilyhenning@gmail.com'
                            onChange={this.onTextChange}
                            messageArr={[ this.state.emailError ]}
                          />
                    </div>
                </div>
                <div className="row">
                    <div className="columns large-offset-11 large-1" style={hideGrid}>
                        <Button
                            children='Add'
                            color='blue'
                            onClick={this.addInfo}
                            style={{color:'blue',borderRadius:5}}
                          />
                    </div>
                </div>
                <div className="row" style={{marginTop:40, marginLeft:10}}>
                    <div className="large-2 columns">
                        <Button
                            children='Back'
                            color='blue-outline'
                            onClick={this.cancelClick}
                            style={{color:'blue',borderRadius:5}}
                          />
                    </div>
                    <div className="large-2 columns">
                        <Button
                            children='Save and Continue'
                            color='blue'
                            onClick={this.saveClick}
                          />
                    </div>
                </div>
            </div>
        );
    }
}

export default ManualEntry;
