import React from 'react';
import Webex from 'webex';

    const webex = window.webex = Webex.init({
      config: {
        credentials: {
          client_id: 'C190e9a362204470aa77adb388e66e78ca175ac471e215ce269540297660be61e',
          redirect_uri: 'http://localhost:3000/addClasses',
          scope: 'spark:all spark:kms'
        }
      }
    });

class Login extends React.component {

    constructor(props) {
        super();
        this.state = {
            loggedIn: false,
            mail: props.mail
        }
    }

    checkWebex() {
        if (webex.canAuthorize) {
              // if already authenticated, logout on click
              webex.logout();
              return true;
            }
         webex.authorization.initiateLogin();
         return false;
    }

    render() {
        return (
            <>
            <h1>{this.state.mail}</h1>
            </>
        );
    }
}
// const Login = (  ) => {
