import React from 'react';
import {
    Input,
    Button,
} from '@momentum-ui/react';

class ManualEntry extends React.Component {
    constructor() {
        super();

        this.state = {
            students: [],
            firstName: '',
            lastName: '',
            email: '',
            lastIndex: 0
        };
        this.addInfo = this.addInfo.bind(this);
        this.onChange = this.onChange.bind(this);
    }

    componentDidMount() {
        const stds = this.state.students.map(item => {
            item.aptId = this.state.lastIndex;
            this.setState({ lastIndex: this.state.lastIndex + 1 });
            return item;
        });
        this.setState({
          students: stds
        });
    }

    addInfo(e) {
        alert(this.state.email);
        let student = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            email: this.state.email
        };
        let temp = this.state.students;
        temp.unshift(student);
        this.setState({
            students: temp
        });
    }

    cancelClick(e) {

    }

    saveClick(e) {

    }

    onChange(e) {
        const target = e.target;
        const value = target.value;
        const name = target.name;

        this.setState({
          [name]: value
        });

    }

    render() {
        const hideGrid = {
            marginTop: 20
        };

        return (
            <div className="grid" style={{paddingTop:20}}>
                <div className="row">
                    <div className="columns large-10" style={hideGrid}>
                        <p style={{fontSize:16}}>Enter Students’ information manually.</p>
                    </div>
                </div>
                <div className="row">
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='firstName'
                            label='First Name'
                            type='text'
                            inputSize='large-15'
                            placeholder='Emily'
                            value={this.firstName}
                            onChange={this.onChange}
                          />
                    </div>
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='lastName'
                            label='Last Name'
                            type='text'
                            inputSize='large-15'
                            placeholder='Henning'
                            value={this.lastName}
                            onChange={this.onChange}
                          />
                    </div>
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='email'
                            label='Email'
                            type='text'
                            inputSize='large-15'
                            placeholder='emilyhenning@gmail.com'
                            value={this.email}
                            onChange={this.onChange}
                          />
                    </div>
                </div>
                <div className="row">
                    <div className="columns large-offset-11 large-1" style={hideGrid}>
                        <Button
                            children='Add'
                            color='blue'
                            onClick={this.addInfo}
                            style={{color:'blue',borderRadius:5}}
                          />
                    </div>
                </div>
                <div className="row" style={{marginTop:40, marginLeft:10}}>
                    <div className="large-2 columns">
                        <Button
                            children='Back'
                            color='blue-outline'
                            onClick={this.cancelClick}
                            style={{color:'blue',borderRadius:5}}
                          />
                    </div>
                    <div className="large-2 columns">
                        <Button
                            children='Save and Continue'
                            color='blue'
                            onClick={this.saveClick}
                          />
                    </div>
                </div>
            </div>
        );
    }
}

export default ManualEntry;
