import React from 'react';
import { Input, Button } from '@momentum-ui/react';

class GeneralForm extends React.Component {
    constructor() {
        super();
        this.state = {
            className: '',
            courseNumber: '',
            period: ''
        }
    }

    render() {
        const hideGrid = {
            background:'white',
            backgroundColor:'transparent',
            borderColor:'transparent'
        };

        return (
            <div className="show-grid" style={{paddingTop:20}}>
                <div className="row">
                    <div className="columns large-11" style={hideGrid}>
                        <p>Enter Class Name, Course Number, and select the Period. This information will be used to create the title of the space that will be
                            generated in Webex Teams. If you need to change anything, you’ll be able to edit this information later.</p>
                    </div>
                </div>
                <div className="row">
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='className'
                            label='Class Name°'
                            type='text'
                            inputSize='large-15'
                            placeholder='e.g. Algebra'
                            value={this.className}
                            style={hideGrid}
                          />
                    </div>
                    <div className="columns large-4" style={hideGrid}>
                    </div>
                    <div className="columns large-4" style={hideGrid}>
                    </div>
                </div>
            </div>
        );
    }
}

export default GeneralForm;
