import React from 'react';
import { Avatar,
        Button,
} from '@momentum-ui/react';
import readXlsxFile from 'read-excel-file'

class FileUpload extends React.Component {
    constructor() {
        super();

        this.state = {
            uploadedFile: '',
        }
        this.fileRef = React.createRef();
        this.uploadClicked = this.uploadClicked.bind(this);
        this.onSelectFile = this.onSelectFile.bind(this);
    }

    uploadClicked(e) {
        if (this.state.uploadedFile === '' ) {
            this.fileRef.current.click();
        } else {
            this.setState({
                uploadedFile: ''
            });
        }
    }

    onSelectFile(e) {
        e.stopPropagation();
        e.preventDefault();
        var file = e.target.files[0];
        this.setState({
            uploadedFile: file.name,
        });
        console.log(this.state.uploadedFile);
        console.log(file.name);
        const schema = {
                        'First Name': {
                            prop: 'firstName',
                            type: String,
                            required: true
                        },
                        'Last Name': {
                            prop: 'lastName',
                            type: String,
                            required: true
                        },
                        'Email': {
                            prop: 'email',
                            type: String,
                            required: true
                        },
                    }
        readXlsxFile(file, { schema })
        .then(({ rows, errors }) => {
        });
    }

    downloadClicked(e) {
        window.location.href=`${process.env.PUBLIC_URL}/studentList.xlsx`;
    }

    deleteClicked(e) {

    }

    cancelClick(e) {

    }

    saveClick(e) {

    }

    render() {
        const hideGrid = {
            marginTop: 20
        };
        const tabBar = {marginTop: 20,
                        height: 40,
                        textAlign: 'center',
                        border: '1px solid #e5e5e5',
        };
        const active = {marginTop: 20,
                        height: 40,
                        textAlign: 'center',
                        border: '1px solid #e5e5e5',
                        backgroundColor: '#e6f9fc'};

        return (
            <div className="grid" style={{paddingTop:20}}>
                <div className="row large-offset-1">
                    <div className="columns large-11" style={hideGrid}>
                        <h1>Classroom Set Up</h1>
                    </div>
                    <div className="columns large-1 columns_new" style={hideGrid}>
                        <Avatar title="Barbara" src="http://react.collab-ui.com/barbara.png"/>
                    </div>
                </div>
                <div className="row large-offset-1">
                    <div className="columns large-3" style={active}>
                            <h3>General Info</h3>
                    </div>
                    <div className="columns large-3" style={tabBar}>
                        <h3>Add Students(Upload)</h3>
                    </div>
                    <div className="columns large-3" style={tabBar}>
                        <h3>Add Students(Manual)</h3>
                    </div>
                    <div className="columns large-3" style={tabBar}>
                        <h3>Review</h3>
                    </div>
                </div>
                <div className="row">
                    <div className="columns large-6" style={hideGrid}>
                        <div className="row" style={{marginLeft: 10}}>
                            <p style={{fontSize:16}}>Upload Students information from an excel file (.XLS or .XLSX). Files
                            must adhere to the format shown to the right.</p>
                        </div>
                        <div className="row" style={{marginTop: 30, marginLeft: 10}}>
                            <t style={this.state.uploadedFile === '' ? {display: "none"} : {marginRight: 20}}>{this.state.uploadedFile}</t>
                            <input type="file" ref={this.fileRef} style={{display: "none"}} onChange={this.onSelectFile}/>
                            <Button
                                children={this.state.uploadedFile === '' ? 'Upload from file' : 'Delete'}
                                color='blue'
                                onClick={this.uploadClicked}
                              />
                              <Button
                                  children='Download Template'
                                  color='blue-outline'
                                  onClick={this.downloadClicked}
                                />
                         </div>
                    </div>
                    <div className="columns large-6" style={hideGrid}>
                        <img  alt="fileImageHolder" src="fileImageHolder.png"/>
                    </div>
                </div>
                <div className="row large-offset-1" style={{marginTop:40, marginLeft:10}}>
                    <div className="large-2 columns">
                        <Button
                            children='Back'
                            color='blue-outline'
                            onClick={this.cancelClick}
                            style={{color:'blue',borderRadius:5}}
                          />
                    </div>
                    <div className="large-2 columns">
                        <Button
                            children='Save and Continue'
                            color='blue'
                            onClick={this.saveClick}
                          />
                    </div>
                </div>
            </div>
        );
    }
}

export default FileUpload;
