import React from 'react';
import {
    Input,
    Button,
    EditableTextfield,
} from '@momentum-ui/react';

class ManualEntry extends React.Component {
    constructor() {
        super();

        this.state = {
            students: [
                        {
                            'firstName': 'Emily',
                            'lastName': 'Henning',
                            'email': 'emilyhenning@gmail.com',
                            'stdId': 1
                        }
                    ],
            firstName: '',
            lastName: '',
            email: '',
            lastIndex: 1
        };
        this.addInfo = this.addInfo.bind(this);
        this.onTextChange = this.onTextChange.bind(this);
    }

    componentDidMount() {
        const stds = this.state.students.map(item => {
            item.stdId = this.state.lastIndex;
            this.setState({ lastIndex: this.state.lastIndex + 1 });
            return item;
        });
        this.setState({
          students: stds
        });
    }

    addInfo(e) {
        let student = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            email: this.state.email,
            stdId: this.state.lastIndex,
        };
        let temp = this.state.students;
        temp.unshift(student);
        this.setState({
            students: temp,
            lastIndex: this.state.lastIndex + 1,
        });
    }

    cancelClick(e) {

    }

    saveClick(e) {

    }

    onTextChange(e) {
        const target = e.target;
        const value = target.value;
        const name = target.name;

        this.setState({
          [name]: value
        });

    }

    updateInfo(name, value, id) {

    }

    deleteInfo(id) {

    }

    render() {
        const hideGrid = {
            marginTop: 20
        };

        return (
            <div className="grid" style={{paddingTop:20}}>
                <div className="row">
                    <div className="columns large-10" style={hideGrid}>
                        <p style={{fontSize:16}}>Enter Students’ information manually.</p>
                    </div>
                </div>
                {this.state.students.map(item => (
                    <div>
                        <div className="row">
                            <div className="columns large-2" style={hideGrid}>
                                <EditableTextfield
                                        handleDoneEditing={(e, data) => this.updateInfo('firstName',data,item.stdId)}
                                        inputText={item.firstName}
                                      />
                            </div>
                            <div className="columns large-2" style={hideGrid}>
                                <EditableTextfield
                                        handleDoneEditing={(e, data) => this.updateInfo('lastName',data,item.stdId)}
                                        inputText={item.lastName}
                                      />
                            </div>
                            <div className="columns large-2" style={hideGrid}>
                                <EditableTextfield
                                        handleDoneEditing={(e, data) => this.updateInfo('email',data,item.stdId)}
                                        inputText={item.email}
                                      />
                            </div>
                            <div className="columns large-5" style={hideGrid}>
                                <Button
                                    children='Delete'
                                    color='blue-outline'
                                    onClick={this.addInfo}
                                    style={{color:'blue',borderRadius:5}}
                                  />
                            </div>
                        </div>
                        <div className="row">
                            <hr style={{marginLeft: 10, height: 2, width: 85, border: 'none', color: 'black', backgroundColor: 'black'}}></hr>
                        </div>
                    </div>
                ))}
                <div className="row">
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='firstName'
                            label='First Name'
                            type='text'
                            inputSize='large-15'
                            placeholder='Emily'
                            onChange={this.onTextChange}
                          />
                    </div>
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='lastName'
                            label='Last Name'
                            type='text'
                            inputSize='large-15'
                            placeholder='Henning'
                            onChange={this.onTextChange}
                          />
                    </div>
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='email'
                            label='Email'
                            type='text'
                            inputSize='large-15'
                            placeholder='emilyhenning@gmail.com'
                            onChange={this.onTextChange}
                          />
                    </div>
                </div>
                <div className="row">
                    <div className="columns large-offset-11 large-1" style={hideGrid}>
                        <Button
                            children='Add'
                            color='blue'
                            onClick={this.addInfo}
                            style={{color:'blue',borderRadius:5}}
                          />
                    </div>
                </div>
                <div className="row" style={{marginTop:40, marginLeft:10}}>
                    <div className="large-2 columns">
                        <Button
                            children='Back'
                            color='blue-outline'
                            onClick={this.cancelClick}
                            style={{color:'blue',borderRadius:5}}
                          />
                    </div>
                    <div className="large-2 columns">
                        <Button
                            children='Save and Continue'
                            color='blue'
                            onClick={this.saveClick}
                          />
                    </div>
                </div>
            </div>
        );
    }
}

export default ManualEntry;
