import React from 'react';
import {
    Link
} from 'react-router-dom';

class Home extends React.Component {
    state = { mail: '' }

    textChange = text =>
        this.setState( {mail: text.target.value} )

    render() {
        return (
            <>
            <div class="home_page_div">
                <section class="home_page_section left">
                    <h4>Cisco Webex Teams </h4>
                    <h2>Seamlessly take your classroom virtual.</h2>
                    <p>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                        Suspendisse molestie in felis id tincidunt. Ut suscipit vestibulum sagittis.
                        Nam ultrices porta sapien, sed tincidunt urna egestas id.
                        Sed finibus felis eget turpis posuere, sed dapibus dui pretium.
                    </p>
                    <p>
                        <input
                        type="text"
                        onChange={this.textChange}
                        />
                    </p>
                    <p>
                    <Link to={{
                        pathname: '/login',
                        mail: this.state.mail
                    }}>
                    <button type="button" className="btn btn-primary"> Start for Free </button>
                    </Link>
                    </p>
                </section>
                <section class="home_page_section">
                </section>
            </div>
            </>
        );
    }
}

export default Home;

