import React from 'react';
import {requestAccessToken} from './Orchestrator.js';
import { Input, Button } from '@momentum-ui/react';

class Login extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            mail: props.location.mail,
            error: true,
        }
        this.onSubmit = this.onSubmit.bind(this);
    }

    componentDidMount(){

    }

    textChange = text => {
        this.setState( {error: false} )
    }

    onSubmit = (event) => {
        requestAccessToken();
        this.props.history.push({
            pathname: '/addClasses',
        });
    }

    render() {
        const mail = this.props.location.mail;
        const centerAllign = {display: 'flex',  justifyContent:'center', alignItems:'center'};
        return (
            <div className="grid">
                <div className="row" style={centerAllign}>
                    <img  src="webex.png" style={{width:100, height:100, marginTop:200}} alt="Webex logo"/>
                </div>
                <div className="row" style={centerAllign}>
                    <t style={{marginTop: 20}}>Hello {mail}</t>
                </div>
                <div className="row" style={centerAllign}>
                    <Input
                        name='defaultInput'
                        type='password'
                        inputSize='large-15'
                        placeholder='Password'
                        onChange={this.textChange}
                        style={{borderRadius:'18px', marginTop: 20}}
                      />
                </div>
                <div className="large-6 columns">
                <Button
                    children='Sign In jfsdjkbfjsdkbvjksdbvlj,sbvjsdbv,sbdkjb'
                    color='blue'
                    size={45}
                    disabled={this.state.error}
                    onClick={this.onSubmit}
                    ariaPressed={false}
                  />
                </div>
                <div className="row" style={centerAllign}>
                    <Button
                        children='Sign In'
                        color='blue'
                        size={45}
                        disabled={this.state.error}
                        onClick={this.onSubmit}
                        ariaPressed={false}
                      />
                </div>
            </div>
        );
    }
}

export default Login;

