import React from 'react';
import { Avatar, Button } from '@momentum-ui/react';
import {
    BrowserRouter as Router,
    Route,
} from 'react-router-dom';
import GeneralForm from './GeneralForm.js';

class Home extends React.Component {

    constructor() {
        super();
        this.state = {
            navigateToNext: false,
        };
        this.addClasses = this.addClasses.bind(this);
    }

    addClasses = (event) => {
        this.props.history.push({
            pathname: '/classroomSetup',
        });
    }

    render() {
        const hideGrid = {
            background:'transparent',
            borderColor:'transparent'
        };

        if (this.state.navigateToNext) {
            return(
                <Route path="/classroomSetup" component={GeneralForm} />
            );
        }

        return (
            <div className="show-grid" style={{paddingTop:50}}>
                <div className="row large-offset-1">
                    <div className="columns large-11" style={hideGrid}>
                        <h1>Classroom Set Up</h1>
                    </div>
                    <div className="columns large-1 columns_new" style={hideGrid}>
                        <Avatar title="Barbara" src="http://react.collab-ui.com/barbara.png"/>
                    </div>
                </div>
                <div className="row large-offset-1">
                    <div className="columns large-6" style={hideGrid}>
                        <t>For each class, we'll walk you through the set-up process. Youll need names and email addresses of students for the set-up process.</t>
                    </div>
                </div>
                <div className="row large-offset-1">
                    <div className="large-3 columns" style={hideGrid}>
                        <Button
                            children='Add Class'
                            color='blue'
                            onClick={this.addClasses}
                          />
                    </div>
                </div>
                <div className="row">
                    <div className="large-8 large-centered columns" style={hideGrid}>
                        <img  alt="addClass" src="addClass.png"/>
                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
