import React from 'react';

class Login extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            mail: props.location.mail,
            loggedIn: true
        }
    }



    render() {

        return (
            <>
                <h1>{this.state.loggedIn ? "Redirecting..." : "Loading..."}</h1>
            </>
        );
    }
}

export default Login;

