import React from 'react';
import { Avatar, Button } from '@momentum-ui/react';
import Header from './Header.js';

const token = RegExp('[#&]' + 'access_token' + '=([^&]*)').exec(window.location.hash);
console.log(token);

class Home extends React.Component {

    constructor(props) {
        super(props);
        this.state = {

        };
        this.addClasses = this.addClasses.bind(this);
    }

    componentDidMount() {
        console.log(token);
        console.log(this.props);
    }

    addClasses = (event) => {
        this.props.history.push({
            pathname: '/classroomSetup',
        });
    }

    render() {
        const hideGrid = {
            background:'transparent',
            borderColor:'transparent'
        };

        return (
            <div className="show-grid" style={{paddingTop:20}}>
                <Header />
                <div className="row large-offset-1">
                    <div className="columns large-6" style={hideGrid}>
                        For each class, we'll walk you through the set-up process. Youll need names and email addresses of students for the set-up process.
                    </div>
                </div>
                <div className="row large-offset-1">
                    <div className="large-3 columns" style={hideGrid}>
                        <Button
                            children='Add Class'
                            color='blue'
                            onClick={this.addClasses}
                            ariaPressed={false}
                          />
                    </div>
                </div>
                <div className="row">
                    <div className="large-8 large-centered columns" style={hideGrid}>
                        <img  alt="addClass" src="addClass.png"/>
                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
