import React from 'react';
import {requestAccessToken} from './Orchestrator.js';
import { Input, Button } from '@momentum-ui/react';

class Login extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            mail: props.location.mail,
            error: true,
        }
        this.onSubmit = this.onSubmit.bind(this);
    }

    componentDidMount(){

    }

    textChange = text => {
        this.setState( {error: false} )
    }

    onSubmit = (event) => {
        requestAccessToken();
        this.props.history.push({
            pathname: '/addClasses',
        });
    }

    render() {
        const mail = this.props.location.mail;
        const centerAllign = {display: 'flex',  justifyContent:'center', alignItems:'center'};
        return (
            <div className="show-grid">
                <div className="row" style={centerAllign}>
                    <img  src="webex.png" style={{width:100, height:100, marginTop:300}} alt="Webex logo"/>
                </div>
                <div className="row" style={{display: 'flex',  justifyContent:'center', alignItems:'center'}}>
                    Hello {mail}
                </div>
            </div>
        );
    }
}

export default Login;

