import React from 'react';
import { Button } from '@momentum-ui/react';
import readXlsxFile from 'read-excel-file'

class FileUpload extends React.Component {
    constructor() {
        super();

        this.state = {
            uploadedFile: null,
            upload: false,
        }
        this.uploadClicked = this.uploadClicked.bind(this);
        this.onSelectFile = this.onSelectFile.bind(this);
    }

    uploadClicked(e) {
        this.refs.fileUploader.click();
    }

    onSelectFile(e) {
        e.stopPropagation();
        e.preventDefault();
        var file = e.target.files[0];
        this.setState({
            uploadedFile: file,
            upload: true,
        });
        const schema = {
                        'First Name': {
                            prop: 'firstName',
                            type: String,
                            required: true
                        },
                        'Last Name': {
                            prop: 'lastName',
                            type: String,
                            required: true
                        },
                        'Email': {
                            prop: 'email',
                            type: String,
                            required: true
                        },
                    }

