    render() {
        const hideGrid = {
            marginTop: 20
        };

        return (
            <div className="grid" style={{paddingTop:20}}>
                <div className="row">
                    <div className="columns large-6" style={hideGrid}>
                        <div className="row" style={{marginLeft: 10}}>
                            <p style={{fontSize:16}}>Upload Students information from an excel file (.XLS or .XLSX). Files
                            must adhere to the format shown to the right.</p>
                        </div>
                        <div className="row" style={{marginTop: 30, marginLeft: 10}}>
                            <t style={this.state.upload ? {} : {display: "none"}}>{this.state.upload}</t>
                            <input type="file" id="file" ref="fileUploader" style={{display: "none"}} onChange={this.onSelectFile}/>
                            <Button
                                children={this.state.upload ? 'Delete' : 'Upload from file'}
                                color='blue'
                                onClick={this.uploadClicked}
                              />
                              <Button
                                  children='Download Template'
                                  color='blue-outline'
                                  onClick={this.downloadClicked}
                                />
                         </div>
                    </div>
                    <div className="columns large-6" style={hideGrid}>
                        <img  alt="fileImageHolder" src="fileImageHolder.png"/>
                    </div>
                </div>
                <div className="row" style={{marginTop:40, marginLeft:10}}>
                    <div className="large-2 columns">
                        <Button
                            children='Back'
                            color='blue-outline'
                            onClick={this.cancelClick}
                            style={{color:'blue',borderRadius:5}}
                          />
                    </div>
                    <div className="large-2 columns">
                        <Button
                            children='Save and Continue'
                            color='blue'
                            onClick={this.saveClick}
                          />
                    </div>
                </div>
            </div>
        );
    }
}

export default FileUpload;
