import React from 'react';
import {
    Avatar,
    Button,
} from '@momentum-ui/react';
import Header from './Header.js';

class Review extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
        };
        this.saveClick = this.saveClick.bind(this);
        this.cancelClick = this.cancelClick.bind(this);
        this.editClass = this.editClass.bind(this);
        this.editStudents = this.editStudents.bind(this);
    }

    editClass(e) {
        this.props.history.push({
            pathname: '/classroomSetup',
            classObj: this.props.location.classObj,
            studentList: this.props.location.studentList,
            uploadedFile: this.props.location.uploadedFile,
        });
    }

    editStudents(e) {
        this.props.history.push({
            pathname: '/addStudents',
            classObj: this.props.location.classObj,
            studentList: this.props.location.studentList,
            uploadedFile: this.props.location.uploadedFile,
        });
    }

    cancelClick(e) {
        this.props.history.push({
            pathname: '/addStudents',
            classObj: this.props.location.classObj,
            studentList: this.props.location.studentList,
            uploadedFile: this.props.location.uploadedFile,
        });
    }

    saveClick(e) {
        this.props.history.push({
            pathname: '/classroomReady',
        });
    }

    render() {
        const hideGrid = {
            marginTop: 20
        };

        const tabBar = {marginTop: 20,
                        height: 40,
                        textAlign: 'center',
                        border: '1px solid #e5e5e5',
        };
        const active = {marginTop: 20,
                        height: 40,
                        textAlign: 'center',
                        border: '1px solid #e5e5e5',
                        backgroundColor: '#e6f9fc'
        };

        let className = '';
        let courseNumber = '';
        let period = '';
        let time = '';
        let recurrence = '';
        let date = '';
        const obj = this.props.location.classObj;
        if (obj !== undefined) {
            className = obj.className;
            courseNumber = obj.courseNumber;
            period = obj.period;
            time= `${obj.startTime} - ${obj.endTime}`;
            recurrence = obj.recurrence;
            date = obj.date;
        }
        let students = [];
        const studentObj = this.props.location.studentList;
        if (studentObj !== undefined) {
            students = studentObj;
        }

        return (
            <div className="grid" style={{paddingTop:20}}>
                <Header />
                <div className="row large-offset-1">
                    <div className="columns large-3" style={tabBar}>
                            <h3>General Info</h3>
                    </div>
                    <div className="columns large-3" style={tabBar}>
                        <h3>Add Students(Upload)</h3>
                    </div>
                    <div className="columns large-3" style={tabBar}>
                        <h3>Add Students(Manual)</h3>
                    </div>
                    <div className="columns large-3" style={active}>
                        <h3>Review</h3>
                    </div>
                </div>
                <div className="row large-offset-1">
                    <div className="columns large-10" style={hideGrid}>
                        <p>Please review the information for the class to finish the set-up process.</p>
                    </div>
                </div>
                <div className="row" style={hideGrid}>
                    <b>General Info:</b>
                </div>
                <div className="row">
                    Class Name: {className}
                </div>
                <div className="row">
                    Course Number: {courseNumber}
                </div>
                <div className="row">
                    Period: {period}
                </div>
                <div className="row">
                    Time: {time}
                </div>
                <div className="row">
                    Recurrence: {recurrence}
                </div>
                <div className="row">
                    Last Day of Class: {date}
                </div>
                <div className="row large-offset-1" style={{marginLeft:10}}>
                    <div className="large-2 columns">
                        <Button
                            children='Edit'
                            color='blue-outline'
                            onClick={this.editClass}
                            style={{color:'blue',borderRadius:5}}
                            ariaPressed={false}
                          />
                  </div>
                </div>
                <div className="row" style={hideGrid}>
                    <b>Students ({students.length} Added):</b>
                </div>
                {students.map(item => (
                    <div  key={item.studentId}>
                        <div className="row"  key={item.studentId}>
                            {item.studentId}. {item.firstName} {item.lastName} {'                '} {item.email}
                        </div>
                    </div>
                ))}
                <div className="row large-offset-1" style={{marginLeft:10}}>
                    <div className="large-2 columns">
                        <Button
                            children='Edit'
                            color='blue-outline'
                            onClick={this.editStudents}
                            style={{color:'blue',borderRadius:5}}
                            ariaPressed={false}
                          />
                  </div>
                </div>
                <div className="row large-offset-1" style={{marginTop:40, marginLeft:10}}>
                    <div className="large-2 columns">
                        <Button
                            children='Back'
                            color='blue-outline'
                            onClick={this.cancelClick}
                            style={{color:'blue',borderRadius:5}}
                            ariaPressed={false}
                          />
                    </div>
                    <div className="large-1 columns">
                        <Button
                            children='Finish'
                            color='blue'
                            onClick={this.saveClick}
                            ariaPressed={false}
                          />
                    </div>
                </div>
            </div>
        );
    }
}

export default Review;
