import React from 'react';
import {
    Input,
    Button,
    Select,
    SelectOption
} from '@momentum-ui/react';

class GeneralForm extends React.Component {
    constructor() {
        super();
        this.state = {
            className: '',
            courseNumber: '',
            period: ''
        }
    }

    onPeriodClick(event, value) {

     }

    render() {
        const hideGrid = {
            // background:'white',
            // borderColor:'white'
            marginTop: 20
        };

        return (
            <div className="grid" style={{paddingTop:20}}>
                <div className="row">
                    <div className="columns large-11" style={hideGrid}>
                        <p>Enter Class Name, Course Number, and select the Period. This information will be used to create the title of the space that will be
                            generated in Webex Teams. If you need to change anything, youâ€™ll be able to edit this information later.</p>
                    </div>
                </div>
                <div className="row">
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='className'
                            label='Class Name*'
                            type='text'
                            inputSize='large-15'
                            placeholder='e.g. Algebra'
                            value={this.className}
                          />
                    </div>
                    <div className="columns large-4" style={hideGrid}>
                        <Input
                            name='courseNumber'
                            label='Course Number*'
                            type='text'
                            inputSize='large-15'
                            placeholder='123456'
                            value={this.courseNumber}
                          />
                    </div>
                    <div className="columns large-4" style={hideGrid}>
                        Period
                        <Select defaultValue='None Selected' style={{backgroundColor:'#ffffff', isActive: 'false', overflow:'auto'}}>
                          <SelectOption value='test1' label='1 Month' />
                          <SelectOption value='test2' label='3 Months' />
                          <SelectOption value='test3' label='6 Months' />
                          <SelectOption value='test4' label='1 Year' />
                        </Select>
                    </div>
                </div>
            </div>
        );
    }
}

export default GeneralForm;
