import React from 'react';

const API_URL = 'https://petee.pegatsdemo.com/prweb/PRRestService/';
const CLIENT_ID = '66093965612664527753';
const CLIENT_SECRET = '32FD9134A62CAEEF681F3E07FBF2A5C7';

export function requestAccessToken() {
  const authInfo = `grant_type=client_credentials&client_id=${CLIENT_ID}&client_secret=${CLIENT_SECRET}`;
  return fetch(`${API_URL}oauth2/v1/token`, {
    method: 'POST',
    headers: new Headers({
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
    body: authInfo,
  })
  .then(response => response.json())
  .then(response => sessionStorage.setItem('access_token',response.access_token));
  console.log(sessionStorage.getItem('access_token'));
    // in your case set state to returned token
}

export function requestTenantInfo(token) {
  return fetch(`${API_URL}ControllerPilot/v1/ViewTenant/d14d10dc-54fd-4b05-8792-b49b66851fa8`, {
    method: 'GET',
    headers: new Headers({
      Authorization: `Bearer ${token}`,
    }),
  })
    .then((response) => response.json());
}
