import React from 'react';
import './App.css';
import Landing from './pages/Landing.js';
import Login from './pages/Login.js';
import Home from './pages/Home.js';
import GeneralForm from './pages/GeneralForm.js';
import FileUpload from './pages/FileUpload.js';
import ManualEntry from './pages/ManualEntry.js';
import Review from './pages/Review.js';
import Complete from './pages/Complete.js';
import NotFound from './pages/NotFound.js'
import {
    BrowserRouter as Router,
    Route,
    Redirect,
    Switch
} from 'react-router-dom';

function PrivateRoute({ children, ...rest }) {
    const isValid = window.sessionStorage.getItem("accessToken");

    return (
        <Route
          {...rest}
          render={({ location }) =>
            isValid ? (
              children
            ) : (
              <Redirect
                to={{
                  pathname: "/",
                  state: { from: location }
                }}
              />
            )
          }
        />
    );
}

class App extends React.Component {

    constructor() {
        super();
    }

    render() {
        return (
            <Router>
                <div className="App">
                  <div id="page-id">
                  <Switch>
                        <Route path="/" component={Landing} exact/>
                        <Route path="/auth" component={Login} />
                        <Route path="/addClasses" component={Home} />
                        <Route path="/classroomSetup" component={GeneralForm} />
                        <Route path="/fileUpload" component={FileUpload} />
                        <Route path="/addStudents" component={ManualEntry} />
                        <PrivateRoute path="/review">
                          <Review />
                        </PrivateRoute>
                        <Route path="/classroomReady" component={Complete} />
                        <Route component={NotFound} />
                    </Switch>
                  </div>
                </div>
            </Router>
        );
    }
}

export default App;

