var fs = require('fs');

var addHeader = function addHeader(file, header) {
  var fileContents = fs.readFileSync(file).toString('utf8');
  var fileWithHeader = header + "\n" + fileContents;
  fs.writeFile(file, fileWithHeader, function (err) {
    if (err) throw err;
    console.log("Header has been added to " + file);
  });
};

module.exports = addHeader;