var fs = require('fs');

var gzipFile = require('@gfx/zopfli').gzip;

var gzip = function gzip(file) {
  var options = {
    verbose: false,
    verbose_more: false,
    numiterations: 15,
    blocksplitting: true,
    blocksplittinglast: false,
    blocksplittingmax: 15
  };
  var fileContents = fs.readFileSync(file).toString('utf8');
  gzipFile(fileContents, options, function (err, output) {
    if (err) throw err;
    fs.writeFile(file + ".gz", output, function (err) {
      if (err) throw err;
      /* eslint-disable no-console */

      console.log(file + " has been gZipped!");
      /* eslaint-enable no-console */
    });
  });
};

module.exports = gzip;